/*
    This module loops through the robots file to populate the home page with card

*/
import React from 'react';
import Card from './Card';//Calling Cards to make the list

const CardList = ({ robots }) => {
    const cardsArray = robots.map((user,i) => {
        return <Card id = {
            robots[i].id
        }
        name = {
            robots[i].name
        }
        email = {
            robots[i].email
        }
        />

    })

    return(// necessary
        <div>
            {cardsArray}
        </div>
    )
}


export default CardList;
